CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_read_matriculas_grado_anio`(
    IN Grado INT,
    IN AnioLectivo INT
)
BEGIN
    SELECT 
		M.*, 
		E.*,
        Tut.*,
        E.Pri_Nombre, 
		E.Seg_Nombre, 
		E.Pri_Apellido, 
		E.Seg_Apellido, 
		E.Telefono, 
        Gen.Genero, 
        Gr.Grado,
        Sec.Seccion, 
        T.Turno, 
		Est.Estado, 
        E.Direccion, 
		A.Anio,
        Tut.Pri_Nombre AS Tutor_Pri_Nombre, 
