		Paren.Parentesco
    FROM 
		Matriculas M
	INNER JOIN 
		Estudiantes E ON M.FK_Estudiante = E.ID_Estudiante
	LEFT JOIN 
        Generos Gen ON E.FK_Genero = Gen.ID_Genero
	INNER JOIN 
		Grados Gr ON M.FK_Grado = Gr.ID_Grado
	INNER JOIN 
